mapper.select:
        select

        from RISK.T_RISK_CUSTOMER_COMMON_RULE
        where use_flag='1' and check_flag='2'
        and ARCHIVED_FLAG='1'

			and EFFECTIVE_DATE<= #{currentDate,jdbcType=VARCHAR} and VALID_DATE>=#{currentDate,jdbcType=VARCHAR}

        and ACCEPT_CHANNEL= #{acceptChannel,jdbcType=VARCHAR} and TRANS_TYPE = #{transType,jdbcType=VARCHAR}
        and ACCOUNT_TYPE=#{accountType,jdbcType=VARCHAR} and USER_GRADE = #{grade,jdbcType=VARCHAR}

        select *  from  (select

        ,rownumber()   over()   AS   rn
        from RISK.T_RISK_CUSTOMER_COMMON_RULE t
        where t.ACCEPT_CHANNEL = #{acceptChannel,jdbcType=VARCHAR}
        and ACCOUNT_TYPE = #{accountType,jdbcType=VARCHAR}
        and ACCOUNT_NO = #{accountNo,jdbcType=VARCHAR}
        and RESV_FLD1= #{resvFld1,jdbcType=VARCHAR}
        and use_flag='1'
        and check_flag='2'
        and ARCHIVED_FLAG='1'

			and EFFECTIVE_DATE<= #{currentDate,jdbcType=TIMESTAMP}
			and VALID_DATE>= #{currentDate,jdbcType=TIMESTAMP}

        )a where  a.rn = 1 order by a.record_no desc

select *  from  (select

,rownumber()   over()   AS   rn
from RISK.T_RISK_CUSTOMER_COMMON_RULE
where use_flag = '1'
and check_flag = '2'
and ARCHIVED_FLAG = '1'

and EFFECTIVE_DATE<=#{currentDate,jdbcType=TIMESTAMP}
and VALID_DATE>=#{currentDate,jdbcType=TIMESTAMP}

and ACCEPT_CHANNEL = #{acceptChannel,jdbcType=VARCHAR}
and rule_type = '1'
and  (ACCOUNT_NO is null or ACCOUNT_NO = '' )
and (TRANS_TYPE is null or TRANS_TYPE = '') 
and USER_GRADE = #{grade,jdbcType=VARCHAR}
and ACCOUNT_TYPE = #{accountType,jdbcType=VARCHAR}

)a where  a.rn <= 1

mapper.update:
update RISK.T_RISK_CUSTOMER_COMMON_RULE t
set t.ARCHIVED_FLAG='2'
where
t.ACCOUNT_NO = #{accountNo,jdbcType=VARCHAR}
and t.ACCOUNT_TYPE = #{accountType,jdbcType=VARCHAR}
and t.USER_GRADE = #{grade,jdbcType=VARCHAR}

    and t.RESV_FLD1 = #{resvFld1,jdbcType=VARCHAR}


    and t.ACCEPT_CHANNEL = #{acceptChannel,jdbcType=VARCHAR}

and use_flag='1'
and check_flag='2'
and ARCHIVED_FLAG='1'
